sp_widget.client_script:
//function() {
  /* widget controller */
//  var c = this;
//}

function($rootScope,$scope) {
	/* widget controller */
	var c = this;
	c.selectItem = function(idx) {
		var selectedNote = c.data.notes[idx];
		var number = selectedNote.number;
		var id = selectedNote.sys_id;
		var noteTitle = selectedNote.title;
		var noteDetail = selectedNote.note;
		console.log("Note ID Emitted: " + id + "\nNote Title: " + noteTitle + "\nNote Details: " + noteDetail + "\nNote Number: " + number);
		$rootScope.noteID = id;
		$rootScope.$emit('selectNote', id);
	}

}

sp_widget.script:
(function() {
	/* populate the 'data' object */
	/* e.g., data.table = $sp.getValue('table'); */

	//create an array to populate without notes
	data.notes = [];
	var noteGR = new GlideRecord('x_snc_createnotes_note');
	noteGR.addQuery('user', gs.getUser().getID());
	noteGR.orderByDesc('sys_created_on');
	noteGR.query();
	while (noteGR.next()) {
		var noteObj = {};
		//use service portal helper method to get some display values
		$sp.getRecordDisplayValues(noteObj, noteGR, 'number,title,sys_id,sys_created_on,sys_updated_on');
		//get the first 20 characters of the description
		noteObj.note = noteGR.getValue('note').slice(0,230);
		//push the populated obj into the array
		data.notes.push(noteObj);
	}
})();
